/* eslint-disable */

import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  let [글제목, 글제목변경] = useState(['강남맛집소개', '여자바지추천', '남자코트추천']);

  let [따봉, 따봉변경] = useState(0);
  let [modal, modal변경] = useState(false);

  let posts = '강남 맛집';

  function 제목바꾸기() {
    var newArray = [...글제목];
    newArray[1] = '빨간맛';
    글제목변경(newArray);
  }

  return (
    <div className="App">
      <div className="black-nav">
        <div>개발 Blog</div>
      </div>
      <button onClick={제목바꾸기}> 코트변경버튼임 </button>
      <div className="list">
        <h3>
          {글제목[0]}
          <span
            onClick={() => {
              따봉변경(따봉 + 1);
            }}
          >
            æ
          </span>{' '}
          {따봉}{' '}
        </h3>
        <p>0217 published</p>
        <hr />
      </div>
      <div className="list">
        <h3>{글제목[1]}</h3>
        <p>0218 published</p>
        <hr />
      </div>
      <div className="list">
        <h3
          onClick={() => {
            modal변경(true);
          }}
        >
          {글제목[2]}
        </h3>
        <p>0219 published</p>
        <hr />
      </div>
      <button
        onClick={() => {
          modal변경(!modal);
        }}
      >
        버튼
      </button>
      {modal === true ? <Modal /> : null}

      {글제목.map(() => {
        return <div>hi</div>;
      })}
    </div>
  );
}

function Modal() {
  return (
    <div className="modal">
      <h2>제목</h2>
      <p>날짜</p>
      <p>상세내용</p>
    </div>
  );
}

export default App;
